import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';
import * as Auth from './Auth.js';

function Login({handleLoginSubmit}) {
    const user = React.useContext(CurrentUserContext);
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    function handleChange(e) {
        setEmail(e.target.value)
        setPassword(e.target.value)
      }

        function handleSubmit(e) {
          e.preventDefault();
          handleLoginSubmit(password, email)
          Auth.authorize();
        } 
    return (
        <main className='sign__content'>
            <div className='sign__container'>
                <h2 className='popup__title popup__title_dark'>Вход</h2>
                <form className='sign__form'>
                    <input className='sign__input' type='email' required minLength="2" placeholder='Email' value={email} onChange={handleChange}></input>
                    <input className='sign__input' type='text' required minLength="6" placeholder='Пароль' value={password} onChange={handleChange}></input>
                    <button className="sign__button" type="submit" onSubmit={handleSubmit}>Войти</button> 
                </form>
            </div>
        </main>
    )
}
export default Login
