import {api} from '../utils/api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';
import { Link } from 'react-router-dom';

function Register({}) {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    function handleChange(e) {
        setEmail(e.target.value)
        setPassword(e.target.value)
      }

        function handleSubmit(e) {
          e.preventDefault();
          
        } 

    return (
        <main className='sign__content'>
            <div className='sign__container'>
                <h2 className='popup__title popup__title_dark'>Регистрация</h2>
                <form className='sign__form'>
                    <input name='email' className='sign__input' type='email' required minLength="2" placeholder='Email' value={email} onChange={handleChange}></input>
                    <input name='password' className='sign__input' type='password' required minLength="6" placeholder='Пароль' value={password} onChange={handleChange}></input>
                    <button className="sign__button" type="submit" onSubmit={handleSubmit}>Зарегистрироваться</button> 
                </form>
                <Link to='/sign-in' className='sign__link'>Уже зарегистрированы? Войти</Link>
            </div>
        </main>
    )
}
export default Register
