function InfoTooltip(props) {
    return (
        <div className={`popup popup_opened ${props.isOpen ? 'popup_opened' : ''}`} onClick={props.onOverlay}>
            <div className="popup__container popup_type_info">
                <button className="button popup__close" type="button" aria-label="Закрыть" onClick={props.onClose}></button>
                <img className="popup__icon" src={props.image} />
                <h2 className="popup__title">{props.text}Вы успешно зарегистрировались!</h2>
                <h2 className="popup__title">Что-то пошло не так! Попробуйте ещё раз.</h2>
            </div>
        </div>
    )
}
export default InfoTooltip
