import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';

function AddPlacePopup({isOpen, onClose, onOverlay, onAddPlace}) {
    
    const [name, setName] = React.useState('')
    const [link, setLink] = React.useState('')
    function handleSubmit(e) {
        e.preventDefault();
        onAddPlace({
            name,
            link
          })
    }

    return (
        <PopupWithForm name="add" title="Новое место" button="Создать" isOpen={isOpen} onClose={onClose} onOverlay={onOverlay} onSubmit={handleSubmit}>
            <input id="place-input" className="popup__input" name="name" type="text" placeholder="Название" required minLength="2" maxLength="30" />
            <span className="popup__error place-input-error"></span>
            <input id="link-input" className="popup__input" name="link" type="url" placeholder="Ссылка на картинку" required />
            <span className="popup__error link-input-error"></span>
        </PopupWithForm>
    )
}
export default AddPlacePopup
