import {api} from '../utils/api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import Card from './Card.js';
import React from 'react';
function Main(props) {
    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api.getInitialCards()
        .then((initialCards) => {
            setCards(initialCards)
        })
        .catch((err) => {
            console.log(`Ошибка: ${err}`);
        })
    });
    const user = React.useContext(CurrentUserContext);
    function handleCardLike(card) {
        const isLiked = card.likes.some((like) => like._id === user._id);
        if (!isLiked) {
            api.putLike(card._id)
            .catch((err) => {
                console.log(`Ошибка: ${err}`);
            })
        } else {
            api.deleteLike(card._id)
            .catch((err) => {
                console.log(`Ошибка: ${err}`);
            })
        }
    };
    function handleCardDelete(card) {
        api.deleteCard(card._id)
        .catch((err) => {
            console.log(`Ошибка: ${err}`);
        })
    }
    return (
        <main className="content">
            <section className="profile">
                <div className="profile__pic" onClick={props.onEditAvatar}>
                    <img className="profile__avatar" alt="Аватар пользователя" src={user.avatar}/>
                </div>
                <div className="profile__info">
                   <div className="profile__heading">
                        <h1 className="profile__name">{user.name}</h1>
                        <button className="button button_open profile__edit-button" onClick={props.onEditProfile} type="button" aria-label="Открыть редактирование профиля"></button>
                    </div>
                    <p className="profile__job">{user.about}</p>
                </div>
                <button className="button button_open profile__add-button" onClick={props.onAddPlace} type="button" aria-label="Добавить место"></button>
            </section>
            <section className="places">
                {cards.map((card) => (
                    <Card key={card._id} card={card} onCardClick={props.onCardClick} onDeleteClick={props.onDeleteClick} onCardLike={handleCardLike}></Card>
                ))}
            </section>
        </main>
    )
}
export default Main
