import {api} from '../utils/api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';
import { Link, withRouter, useHistory } from 'react-router-dom';
import * as Auth from './Auth.js';

function Register({}) {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    function handleEmailChange(e) {
        setEmail(e.target.value)
    }

    function handlePasswordChange(e) {
        setPassword(e.target.value)
    }
    const history = useHistory(); 
    function handleSubmit(e) {
        e.preventDefault();
        Auth.register(password, email)
        .then(() => {
            history.push('/sign-in')
        })

        .catch((err) => console.log(err));
    } 

    return (
        <main className='sign__content'>
            <div className='sign__container'>
                <h2 className='popup__title popup__title_dark'>Регистрация</h2>
                <form className='sign__form'>
                    <input name='email' className='sign__input' type='email' required minLength="2" placeholder='Email' value={email} onChange={handleEmailChange}></input>
                    <input name='password' className='sign__input' type='password' required minLength="6" placeholder='Пароль' value={password} onChange={handlePasswordChange}></input>
                    <button className="sign__button" type="submit" onSubmit={handleSubmit}>Зарегистрироваться</button> 
                </form>
                <Link to='/sign-in' className='sign__link'>Уже зарегистрированы? Войти</Link>
            </div>
        </main>
    )
}
export default withRouter(Register)
