import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';

function EditAvatarPopup({isOpen, onClose, onOverlay, onUpdateAvatar}) {
    
    const [avatar, setAvatar] = React.useState()
    const user = React.useContext(CurrentUserContext);
    React.useEffect(() => {
        setAvatar(user.avatar);
      }, [user]);
      const avatarRef = React.useRef(); 
      function handleSubmit(e) {
        e.preventDefault();
        onUpdateAvatar({
          avatar: avatarRef
        });
      } 

    return (
        <PopupWithForm name="pic" title="Обновить аватар" button="Сохранить" isOpen={isOpen} onClose={onClose} onOverlay={onOverlay} onSubmit={handleSubmit}>
          <input id="pic-input" className="popup__input" name="avatar" type="url" placeholder="Ссылка на картинку" minLength="2" required />
          <span className="popup__error pic-input-error"></span>
        </PopupWithForm>
    )
}

export default EditAvatarPopup
