import Header from './components/Header.js';
import Main from './components/Main.js';
import Footer from './components/Footer.js';
import PopupWithForm from './components/PopupWithForm.js';
import ImagePopup from './components/ImagePopup.js';

function handleEditAvatarClick() {
      document.querySelector('.popup_type_pic').classList.add('popup_opened')
}
function handleEditProfileClick() {
      document.querySelector('.popup_type_edit').classList.add('popup_opened')
}
function handleAddPlaceClick() {
      document.querySelector('.popup_type_add').classList.add('popup_opened')
}

function App() {
  return (
    <body className="page">
    <div className="page__container">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick()} onEditProfile={handleEditProfileClick()} onAddPlace={handleAddPlaceClick()} />
      <Footer />
      <PopupWithForm name="edit" title="Редактировать профиль" button="Сохранить" children={`<input id="name-input" className="popup__input" name="name" type="text" placeholder="Имя" required minLength="2" maxLength="40" />
                   <span className="popup__error name-input-error"></span>
                   <input id="job-input" className="popup__input" name="about" type="text" placeholder="О себе" required minLength="2" maxLength="200" />
                   <span className="popup__error job-input-error"></span>`}/>
      <PopupWithForm name="pic" title="Обновить аватар" button="Сохранить" children={`<input id="pic-input" className="popup__input" name="avatar" type="url" placeholder="Ссылка на картинку" minLength="2" required />
                   <span className="popup__error pic-input-error"></span>`}/>
      <PopupWithForm name="add" title="Новое место" button="Создать" children={`<input id="place-input" className="popup__input" name="name" type="text" placeholder="Название" required minLength="2" maxLength="30" />
                   <span className="popup__error place-input-error"></span>
                   <input id="link-input" className="popup__input" name="link" type="url" placeholder="Ссылка на картинку" required />
                   <span className="popup__error link-input-error"></span>`}/>
      <PopupWithForm name="confirm" title="Вы уверены?" button="Да"/>
      <ImagePopup />
        <template id="template">
            <div className="place">
                <img className="place__image" />
                <button className="button place__delete-button" type="button" aria-label="Удалить"></button>
                <div className="place__caption">
                    <h2 className="place__title"></h2>
                    <div className="place__like">
                        <button className="place__like-button" type="button" aria-label="Лайкнуть"></button>
                        <span className="place__like-number"></span>
                    </div>

                </div>
            </div>
        </template>
    </div>

</body>
  );
}

export default App;
