import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import React from 'react';

function ConfirmationPopup({card, isOpen, onClose, onOverlay, DeleteCard}) {

    function handleSubmit(e) {
      e.preventDefault();
      DeleteCard(card);
    } 

    return (
        <PopupWithForm name="confirm" title="Вы уверены?" button="Да"  isOpen={isOpen} onClose={onClose} onOverlay={onOverlay} onSubmit={handleSubmit}/>
    )
}

export default ConfirmationPopup
