import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import {api} from '../utils/api.js';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import ImagePopup from './ImagePopup.js';
import React from 'react';

function App() {
  const [currentUser, setCurrentUser] = React.useState('');
  React.useEffect(() => {
    api.getUserInfo()
    .then((info) => {
      setCurrentUser(info)
    })
    .catch((err) => {
      console.log(`Ошибка: ${err}`);
  })
  })

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  const [isConfirmationPopupOpen, setIsConfirmationPopupOpen] = React.useState(false);
  function handleDeleteClick() {
    setIsConfirmationPopupOpen(true);
  }

  const [selectedCard, setSelectedCard] = React.useState();
  function handleCardClick(card) {
    setSelectedCard(card)
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsConfirmationPopupOpen(false);
    setSelectedCard(null);
  }

  function closeOnOverlayClick(evt) {
    if (evt.target.classList.contains('popup')) {
        closeAllPopups();
    } 
}
  function handleUpdateUser({name, about}) {
    api.editProfile({name, about})
    .then((info) => {
      setCurrentUser(info)
    })
    .catch((err) => {
      console.log(`Ошибка: ${err}`);
    })
    closeAllPopups()
  }
  function handleUpdateAvatar({avatar}) {
    api.editAvatar({avatar})
    .then((info) => {
      setCurrentUser(info)
    })
    .catch((err) => {
      console.log(`Ошибка: ${err}`);
    })
    closeAllPopups()
  }
  return (
    <div className="page">
      <div className="page__container">
        <CurrentUserContext.Provider value={currentUser}>
          <Header />
          <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onCardClick={handleCardClick} onDeleteClick={handleDeleteClick}/>
          <Footer />
          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onOverlay={closeOnOverlayClick} onUpdateUser={handleUpdateUser} />
          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onOverlay={closeOnOverlayClick} onUpdateAvatar={handleUpdateAvatar} />  
          <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onOverlay={closeOnOverlayClick}  />
          <PopupWithForm name="confirm" title="Вы уверены?" button="Да" isOpen={isConfirmationPopupOpen} onClose={closeAllPopups} onOverlay={closeOnOverlayClick}/>
          <ImagePopup card={selectedCard} onClose={closeAllPopups} onOverlay={closeOnOverlayClick}/>
        </CurrentUserContext.Provider>
      </div>
    </div>
  );
}

export default App;
